package Arrays;

public class finidninganumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int c[] = { 2, 5, 6, 7, 8 };
		
		for(int i=0;i<c.length;i++)// 5 times
		{
			if(c[i]==7)
			{
				System.out.println(i);
			}	
			
		}
			
	}

}