package seleniummay;

public class kshapeProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int row = 9; row >= 1; row = row-2) 
		{
			for (int col = 1; col <= row; col = col+2) {
				System.out.print(col);
			}
				System.out.println();
		}
		for (int row = 9; row >= 1	; row = row-2) 
		{
			for (int col = 1; col <= row; col = col+2) {
				System.out.print(col);
			}
				System.out.println();
		}

	}
}
